import { newStorageHelpers } from '../utils/newStorage'

import { getChromeAPI } from './utils'

export function setupMessageListener() {
  const chromeAPI = getChromeAPI()
  if (chromeAPI && chromeAPI.runtime && chromeAPI.runtime.onMessage) {
    chromeAPI.runtime.onMessage.addListener((message: any, _sender: any, sendResponse: any) => {
      console.log('Background received message:', message)

      switch (message.type) {
        case 'GET_EMOJI_DATA':
          handleGetEmojiData(sendResponse)
          return true

        case 'SAVE_EMOJI_DATA':
          handleSaveEmojiData(message.data, sendResponse)
          return true

        case 'SYNC_SETTINGS':
          handleSyncSettings(message.settings, sendResponse)
          return true

        default:
          console.log('Unknown message type:', message.type)
          sendResponse({ success: false, error: 'Unknown message type' })
      }

      if (message.action) {
        switch (message.action) {
          case 'addToFavorites':
            handleAddToFavorites(message.emoji, sendResponse)
            return true

          case 'addEmojiFromWeb':
            handleAddEmojiFromWeb(message.emojiData, sendResponse)
            return true

          default:
            console.log('Unknown action:', message.action)
            sendResponse({ success: false, error: 'Unknown action' })
        }
      }
    })
  }
}

export async function handleAddEmojiFromWeb(emojiData: any, sendResponse: (response: any) => void) {
  try {
    // èŽ·å–æ‰€æœ‰è¡¨æƒ…ç»„
    const groups = await newStorageHelpers.getAllEmojiGroups()

    // æ‰¾åˆ°æœªåˆ†ç»„è¡¨æƒ…ç»„
    let ungroupedGroup = groups.find((g: any) => g.id === 'ungrouped')
    if (!ungroupedGroup) {
      // å¦‚æžœæœªåˆ†ç»„è¡¨æƒ…ç»„ä¸å­˜åœ¨ï¼Œåˆ›å»ºä¸€ä¸ª
      ungroupedGroup = {
        id: 'ungrouped',
        name: 'æœªåˆ†ç»„',
        icon: 'ðŸ“¦',
        order: 999,
        emojis: []
      }
      groups.push(ungroupedGroup)
    }

    // æ£€æŸ¥æ˜¯å¦å·²å­˜åœ¨ç›¸åŒURLçš„è¡¨æƒ…
    const existingEmoji = ungroupedGroup.emojis.find((e: any) => e.url === emojiData.url)
    if (existingEmoji) {
      sendResponse({ success: false, error: 'æ­¤è¡¨æƒ…å·²å­˜åœ¨äºŽæœªåˆ†ç»„ä¸­' })
      return
    }

    // åˆ›å»ºæ–°è¡¨æƒ…
    const newEmoji = {
      id: `emoji-${Date.now()}-${Math.random().toString(36).slice(2, 8)}`,
      packet: Date.now(),
      name: emojiData.name,
      url: emojiData.url,
      groupId: 'ungrouped',
      addedAt: Date.now()
    }

    ungroupedGroup.emojis.push(newEmoji)

    // ä¿å­˜åˆ°å­˜å‚¨
    await newStorageHelpers.setAllEmojiGroups(groups)

    console.log('[Background] æˆåŠŸæ·»åŠ è¡¨æƒ…åˆ°æœªåˆ†ç»„:', newEmoji.name)
    sendResponse({ success: true, message: 'è¡¨æƒ…å·²æ·»åŠ åˆ°æœªåˆ†ç»„' })
  } catch (error) {
    console.error('[Background] æ·»åŠ è¡¨æƒ…å¤±è´¥:', error)
    sendResponse({ success: false, error: error instanceof Error ? error.message : 'æ·»åŠ å¤±è´¥' })
  }
}

export async function handleAddToFavorites(emoji: any, sendResponse: (response: any) => void) {
  try {
    // Use the unified newStorageHelpers to read/update groups for consistency
    const groups = await newStorageHelpers.getAllEmojiGroups()
    const favoritesGroup = groups.find((g: any) => g.id === 'favorites')
    if (!favoritesGroup) {
      console.warn('Favorites group not found - creating one')
      const newFavorites = { id: 'favorites', name: 'Favorites', icon: 'â­', order: 0, emojis: [] }
      groups.unshift(newFavorites)
    }

    const finalGroups = groups
    const favGroup = finalGroups.find((g: any) => g.id === 'favorites')!

    const now = Date.now()
    const existingEmojiIndex = favGroup.emojis.findIndex((e: any) => e.url === emoji.url)

    if (existingEmojiIndex !== -1) {
      const existingEmoji = favGroup.emojis[existingEmojiIndex]
      const lastUsed = existingEmoji.lastUsed || 0
      const timeDiff = now - lastUsed
      const twelveHours = 12 * 60 * 60 * 1000

      if (timeDiff < twelveHours) {
        existingEmoji.usageCount = (existingEmoji.usageCount || 0) + 1
      } else {
        const currentCount = existingEmoji.usageCount || 1
        existingEmoji.usageCount = Math.floor(currentCount * 0.8) + 1
        existingEmoji.lastUsed = now
      }
    } else {
      const favoriteEmoji = {
        ...emoji,
        id: `fav-${Date.now()}-${Math.random().toString(36).slice(2, 8)}`,
        groupId: 'favorites',
        usageCount: 1,
        lastUsed: now,
        addedAt: now
      }
      favGroup.emojis.push(favoriteEmoji)
    }

    favGroup.emojis.sort((a: any, b: any) => (b.lastUsed || 0) - (a.lastUsed || 0))

    // Persist via newStorageHelpers which updates group index and individual groups
    await newStorageHelpers.setAllEmojiGroups(finalGroups)

    // Notify content scripts by updating chrome.storage (legacy compatibility)
    const chromeAPI = getChromeAPI()
    if (chromeAPI && chromeAPI.storage && chromeAPI.storage.local) {
      try {
        await new Promise<void>((resolve, reject) => {
          chromeAPI.storage.local.set({ emojiGroups: finalGroups }, () => {
            if (chromeAPI.runtime.lastError) reject(chromeAPI.runtime.lastError)
            else resolve()
          })
        })
      } catch (e) {
        // ignore
      }
    }

    sendResponse({ success: true, message: 'Added to favorites' })
  } catch (error) {
    console.error('Failed to add emoji to favorites:', error)
    sendResponse({
      success: false,
      error: error instanceof Error ? error.message : 'Unknown error'
    })
  }
}

export async function handleGetEmojiData(sendResponse: (response: any) => void) {
  const chromeAPI = getChromeAPI()
  if (!chromeAPI || !chromeAPI.storage) {
    sendResponse({ success: false, error: 'Chrome storage API not available' })
    return
  }

  try {
    const data = await chromeAPI.storage.local.get(['emojiGroups', 'appSettings', 'favorites'])

    // è§£æžæ–°çš„å­˜å‚¨æ ¼å¼ï¼ŒappSettings çŽ°åœ¨æ˜¯ { data: {...}, timestamp: ... } çš„æ ¼å¼
    let settings = {}
    if (data.appSettings) {
      if (data.appSettings.data && typeof data.appSettings.data === 'object') {
        // æ–°æ ¼å¼ï¼š{ data: {...}, timestamp: ... }
        settings = data.appSettings.data
      } else if (typeof data.appSettings === 'object') {
        // å…¼å®¹æ—§æ ¼å¼ï¼šç›´æŽ¥æ˜¯è®¾ç½®å¯¹è±¡
        settings = data.appSettings
      }
    }

    sendResponse({
      success: true,
      data: {
        groups: data.emojiGroups || [],
        settings: settings,
        favorites: data.favorites || []
      }
    })
  } catch (error: any) {
    console.error('Failed to get emoji data:', error)
    sendResponse({ success: false, error: error.message })
  }
}

export async function handleSaveEmojiData(data: any, sendResponse: (response: any) => void) {
  const chromeAPI = getChromeAPI()
  if (!chromeAPI || !chromeAPI.storage) {
    sendResponse({ success: false, error: 'Chrome storage API not available' })
    return
  }

  try {
    await chromeAPI.storage.local.set(data)
    sendResponse({ success: true })
  } catch (error: any) {
    console.error('Failed to save emoji data:', error)
    sendResponse({ success: false, error: error.message })
  }
}

export async function handleSyncSettings(settings: any, sendResponse: (response: any) => void) {
  const chromeAPI = getChromeAPI()
  if (!chromeAPI || !chromeAPI.storage || !chromeAPI.tabs) {
    sendResponse({ success: false, error: 'Chrome API not available' })
    return
  }

  try {
    // ä¿å­˜ä¸ºæ–°çš„å­˜å‚¨æ ¼å¼ï¼š{ data: {...}, timestamp: ... }
    const timestamp = Date.now()
    const appSettingsData = {
      data: { ...settings, lastModified: timestamp },
      timestamp: timestamp
    }

    await chromeAPI.storage.local.set({ appSettings: appSettingsData })

    const tabs = await chromeAPI.tabs.query({})
    for (const tab of tabs) {
      if (tab.id) {
        chromeAPI.tabs
          .sendMessage(tab.id, {
            type: 'SETTINGS_UPDATED',
            settings: settings
          })
          .catch(() => {
            // Ignore errors for tabs that don't have content script
          })
      }
    }

    sendResponse({ success: true })
  } catch (error: any) {
    console.error('Failed to sync settings:', error)
    sendResponse({ success: false, error: error.message })
  }
}

export function setupStorageChangeListener() {
  const chromeAPI = getChromeAPI()
  if (chromeAPI && chromeAPI.storage && chromeAPI.storage.onChanged) {
    chromeAPI.storage.onChanged.addListener((changes: any, namespace: any) => {
      console.log('Storage changed:', changes, namespace)
      // Placeholder for cloud sync or other reactions
    })
  }
}

export function setupContextMenu() {
  const chromeAPI = getChromeAPI()
  if (chromeAPI && chromeAPI.runtime && chromeAPI.runtime.onInstalled && chromeAPI.contextMenus) {
    chromeAPI.runtime.onInstalled.addListener(() => {
      chrome.storage.local.get('appSettings', result => {
        // è§£æžæ–°çš„å­˜å‚¨æ ¼å¼æ¥èŽ·å– forceMobileMode
        let forceMobileMode = false
        if (result.appSettings) {
          if (result.appSettings.data && typeof result.appSettings.data === 'object') {
            // æ–°æ ¼å¼ï¼š{ data: {...}, timestamp: ... }
            forceMobileMode = result.appSettings.data.forceMobileMode || false
          } else if (typeof result.appSettings === 'object') {
            // å…¼å®¹æ—§æ ¼å¼ï¼šç›´æŽ¥æ˜¯è®¾ç½®å¯¹è±¡
            forceMobileMode = result.appSettings.forceMobileMode || false
          }
        }

        if (chromeAPI.contextMenus && chromeAPI.contextMenus.create) {
          chromeAPI.contextMenus.create({
            id: 'open-emoji-options',
            title: 'è¡¨æƒ…ç®¡ç†',
            contexts: ['page']
          })
          chromeAPI.contextMenus.create({
            id: 'force-mobile-mode',
            title: 'å¼ºåˆ¶ä½¿ç”¨ç§»åŠ¨æ¨¡å¼',
            type: 'checkbox',
            checked: forceMobileMode,
            contexts: ['page']
          })
        }
      })
    })

    if (chromeAPI.contextMenus.onClicked) {
      chromeAPI.contextMenus.onClicked.addListener((info: any, _tab: any) => {
        if (
          info.menuItemId === 'open-emoji-options' &&
          chromeAPI.runtime &&
          chromeAPI.runtime.openOptionsPage
        ) {
          chromeAPI.runtime.openOptionsPage()
        } else if (info.menuItemId === 'force-mobile-mode') {
          const newCheckedState = info.checked

          // èŽ·å–å½“å‰è®¾ç½®å¹¶æ›´æ–° forceMobileMode
          chrome.storage.local.get('appSettings', result => {
            let currentSettings = {}
            if (result.appSettings) {
              if (result.appSettings.data && typeof result.appSettings.data === 'object') {
                currentSettings = result.appSettings.data
              } else if (typeof result.appSettings === 'object') {
                currentSettings = result.appSettings
              }
            }

            // æ›´æ–°è®¾ç½®å¹¶ä¿å­˜ä¸ºæ–°æ ¼å¼
            const timestamp = Date.now()
            const updatedSettings = {
              ...currentSettings,
              forceMobileMode: newCheckedState,
              lastModified: timestamp
            }

            const appSettingsData = {
              data: updatedSettings,
              timestamp: timestamp
            }

            chrome.storage.local.set({ appSettings: appSettingsData })
          })
        }
      })
    }
  }
}

export function setupPeriodicCleanup() {
  setInterval(
    async () => {
      const chromeAPI = getChromeAPI()
      if (!chromeAPI || !chromeAPI.storage) return

      try {
        const data = await chromeAPI.storage.local.get(['emojiGroups'])
        if (data.emojiGroups) {
          console.log('Storage cleanup check completed')
        }
      } catch (error) {
        console.error('Storage cleanup error:', error)
      }
    },
    24 * 60 * 60 * 1000
  )
}

import { defaultEmojiGroups } from '../types/defaultEmojiGroups'

import { getChromeAPI } from './utils'

export async function initializeDefaultData() {
  const chromeAPI = getChromeAPI()
  if (!chromeAPI || !chromeAPI.storage) {
    console.error('Chrome storage API not available')
    return
  }

  try {
    const existingData = await chromeAPI.storage.local.get(['emojiGroups', 'appSettings'])

    if (!existingData.emojiGroups) {
      await chromeAPI.storage.local.set({ emojiGroups: defaultEmojiGroups })
      console.log('Default emoji groups initialized')
    }

    if (!existingData.appSettings) {
      const defaultSettings = {
        imageScale: 100,
        defaultGroup: 'nachoneko',
        showSearchBar: true,
        gridColumns: 4
      }

      await chromeAPI.storage.local.set({ appSettings: defaultSettings })
      console.log('Default app settings initialized')
    }
  } catch (error) {
    console.error('Failed to initialize default data:', error)
  }
}

export function setupOnInstalledListener() {
  const chromeAPI = getChromeAPI()
  if (chromeAPI && chromeAPI.runtime && chromeAPI.runtime.onInstalled) {
    chromeAPI.runtime.onInstalled.addListener(async (details: any) => {
      console.log('Emoji extension installed/updated:', details.reason)
      if (details.reason === 'install') {
        await initializeDefaultData()
      }
    })
  }
}

// Utilities for background scripts
export const getChromeAPI = () => {
  try {
    return (globalThis as any).chrome || (self as any).chrome
  } catch (e) {
    console.error('Chrome API not available:', e)
    return null
  }
}

import { ref, computed } from 'vue'

import { ProviderManager } from '@/utils/imageProviders'
import type { GenerateRequest } from '@/types/imageGenerator'

export function useImageGenerator() {
  const providerManager = new ProviderManager()
  const isGenerating = ref(false)
  const error = ref<string | null>(null)
  const generatedImages = ref<string[]>([])

  // Form data
  const generationMode = ref<'generate' | 'edit'>('generate')
  const uploadedImage = ref<string | undefined>()
  const prompt = ref('')
  const generationConfig = ref({
    imageCount: 4,
    aspectRatio: '1:1',
    style: ''
  })

  // Computed
  const canGenerate = computed(() => {
    const hasPrompt = prompt.value.trim().length > 0
    const currentProvider = providerManager.getCurrentProvider()
    const hasApiKey =
      currentProvider && typeof currentProvider.loadApiKey === 'function'
        ? currentProvider.loadApiKey().length > 0
        : false
    const hasImageForEdit = generationMode.value === 'edit' ? !!uploadedImage.value : true

    return hasPrompt && hasApiKey && hasImageForEdit && !isGenerating.value
  })

  // Methods
  const generateImages = async () => {
    if (!canGenerate.value) return

    isGenerating.value = true
    error.value = null
    generatedImages.value = []

    try {
      const request: GenerateRequest = {
        prompt: prompt.value.trim(),
        aspectRatio: generationConfig.value.aspectRatio,
        numberOfImages: generationConfig.value.imageCount,
        style: generationConfig.value.style || undefined,
        editMode: generationMode.value === 'edit',
        inputImage: uploadedImage.value
      }

      const images = await providerManager.generateImages(request)
      generatedImages.value = images
    } catch (err: any) {
      error.value = err.message || 'ç”Ÿæˆå›¾ç‰‡æ—¶å‘ç”Ÿé”™è¯¯ï¼Œè¯·ç¨åŽé‡è¯•'
      console.error('Generation failed:', err)
    } finally {
      isGenerating.value = false
    }
  }

  const clearResults = () => {
    generatedImages.value = []
    error.value = null
  }

  const downloadImage = async (url: string, filename: string) => {
    try {
      await providerManager.downloadImage(url, filename)
    } catch (err: any) {
      throw new Error(err.message || 'ä¸‹è½½å¤±è´¥')
    }
  }

  const copyImageUrl = async (url: string) => {
    try {
      await providerManager.copyToClipboard(url)
    } catch (err) {
      throw new Error('å¤åˆ¶å¤±è´¥')
    }
  }

  const resetForm = () => {
    generationMode.value = 'generate'
    uploadedImage.value = undefined
    prompt.value = ''
    generationConfig.value = {
      imageCount: 4,
      aspectRatio: '1:1',
      style: ''
    }
    clearResults()
  }

  return {
    // State
    providerManager,
    isGenerating,
    error,
    generatedImages,
    generationMode,
    uploadedImage,
    prompt,
    generationConfig,

    // Computed
    canGenerate,

    // Methods
    generateImages,
    clearResults,
    downloadImage,
    copyImageUrl,
    resetForm
  }
}

// Build-time flags for development and production
// These values are replaced at build time by Vite

declare global {
  const __ENABLE_LOGGING__: boolean
  const __ENABLE_INDEXEDDB__: boolean
}

// Runtime flags that can be set by build configuration
export const BUILD_FLAGS = {
  // æ˜¯å¦å¯ç”¨æ—¥å¿—è¾“å‡º (æŽ§åˆ¶ console.log, console.warn, console.error)
  ENABLE_LOGGING: typeof __ENABLE_LOGGING__ !== 'undefined' ? __ENABLE_LOGGING__ : true,

  // æ˜¯å¦å¯ç”¨ IndexedDB è¯»å†™æ“ä½œ
  ENABLE_INDEXEDDB: typeof __ENABLE_INDEXEDDB__ !== 'undefined' ? __ENABLE_INDEXEDDB__ : true
} as const

// æ—¥å¿—åŒ…è£…å™¨ï¼Œæ ¹æ®ç¼–è¯‘æœŸæ ‡å¿—å†³å®šæ˜¯å¦è¾“å‡º
export const logger = {
  log: (...args: any[]) => {
    if (BUILD_FLAGS.ENABLE_LOGGING) {
      console.log(...args)
    }
  },

  warn: (...args: any[]) => {
    if (BUILD_FLAGS.ENABLE_LOGGING) {
      console.warn(...args)
    }
  },

  error: (...args: any[]) => {
    if (BUILD_FLAGS.ENABLE_LOGGING) {
      console.error(...args)
    }
  },

  // å¼€å‘æ¨¡å¼æ—¥å¿—ï¼ˆåªåœ¨å¼€å‘çŽ¯å¢ƒä¸‹è¾“å‡ºï¼‰
  dev: (...args: any[]) => {
    if (BUILD_FLAGS.ENABLE_LOGGING && process.env.NODE_ENV === 'development') {
      console.log('[DEV]', ...args)
    }
  }
}

// IndexedDB æ“ä½œåŒ…è£…å™¨
export const indexedDBWrapper = {
  isEnabled: () => BUILD_FLAGS.ENABLE_INDEXEDDB,

  // æ£€æŸ¥æ˜¯å¦åº”è¯¥è·³è¿‡ IndexedDB æ“ä½œ
  shouldSkip: () => !BUILD_FLAGS.ENABLE_INDEXEDDB
}

import { defaultSettings } from '../types/emoji'

export class ContentStorageAdapter {
  // Read from extension storage with fallback to local/session storage
  async get(key: string): Promise<any> {
    // Try extension storage first (main source for content scripts)
    if (chrome?.storage?.local) {
      try {
        const result = await chrome.storage.local.get({ [key]: null })
        const value = result[key]
        if (value !== null && value !== undefined) {
          console.log(`[Content Storage] Found ${key} in extension storage`)
          // Handle both new storage format (with .data) and legacy format
          if (value && typeof value === 'object' && value.data !== undefined) {
            return value.data
          }
          return value
        }
      } catch (error) {
        console.warn(`[Content Storage] Extension storage failed for ${key}:`, error)
      }
    }

    // Fallback to localStorage
    try {
      if (typeof localStorage !== 'undefined') {
        const value = localStorage.getItem(key)
        if (value) {
          const parsed = JSON.parse(value)
          if (parsed !== null && parsed !== undefined) {
            console.log(`[Content Storage] Found ${key} in localStorage`)
            // Handle both new storage format (with .data) and legacy format
            if (parsed && typeof parsed === 'object' && parsed.data !== undefined) {
              return parsed.data
            }
            return parsed
          }
        }
      }
    } catch (error) {
      console.warn(`[Content Storage] localStorage failed for ${key}:`, error)
    }

    // Fallback to sessionStorage
    try {
      if (typeof sessionStorage !== 'undefined') {
        const value = sessionStorage.getItem(key)
        if (value) {
          const parsed = JSON.parse(value)
          if (parsed !== null && parsed !== undefined) {
            console.log(`[Content Storage] Found ${key} in sessionStorage`)
            // Handle both new storage format (with .data) and legacy format
            if (parsed && typeof parsed === 'object' && parsed.data !== undefined) {
              return parsed.data
            }
            return parsed
          }
        }
      }
    } catch (error) {
      console.warn(`[Content Storage] sessionStorage failed for ${key}:`, error)
    }

    console.log(`[Content Storage] No data found for ${key}`)
    return null
  }

  async getAllEmojiGroups(): Promise<any[]> {
    console.log('[Content Storage] Getting all emoji groups')

    // First try to get the group index
    const groupIndex = await this.get('emojiGroupIndex')
    console.log('[Content Storage] Group index:', groupIndex)

    if (groupIndex && Array.isArray(groupIndex) && groupIndex.length > 0) {
      const groups = []
      for (const groupInfo of groupIndex) {
        console.log(`[Content Storage] Processing group info:`, groupInfo)
        if (groupInfo && groupInfo.id) {
          const group = await this.get(`emojiGroup_${groupInfo.id}`)
          console.log(`[Content Storage] Raw group data for ${groupInfo.id}:`, group)

          if (group) {
            console.log(`[Content Storage] Group structure:`, {
              hasEmojis: !!group.emojis,
              emojisType: typeof group.emojis,
              isArray: Array.isArray(group.emojis),
              emojisLength: group.emojis?.length,
              groupKeys: Object.keys(group)
            })

            // Handle case where emojis is stored as an object instead of array
            let emojisArray = group.emojis
            if (group.emojis && typeof group.emojis === 'object' && !Array.isArray(group.emojis)) {
              // Convert object to array if needed
              emojisArray = Object.values(group.emojis)
              console.log(
                `[Content Storage] Converting emojis object to array for ${group.name}, length: ${emojisArray.length}`
              )
            }

            if (emojisArray && Array.isArray(emojisArray)) {
              const processedGroup = { ...group, emojis: emojisArray, order: groupInfo.order || 0 }
              groups.push(processedGroup)
              console.log(
                `[Content Storage] âœ… Loaded group: ${group.name} with ${emojisArray.length} emojis`
              )
            } else if (groupInfo.id === 'favorites') {
              // Special handling for favorites group which might not have emojis initially
              const favoritesGroup = {
                ...group,
                emojis: emojisArray && Array.isArray(emojisArray) ? emojisArray : [],
                order: groupInfo.order || 0
              }
              groups.push(favoritesGroup)
              console.log(
                `[Content Storage] âœ… Loaded favorites group with ${favoritesGroup.emojis.length} emojis`
              )
            } else {
              console.warn(
                `[Content Storage] âŒ Group ${group.name || groupInfo.id} has invalid emojis after conversion:`,
                {
                  hasEmojis: !!emojisArray,
                  emojisType: typeof emojisArray,
                  isArray: Array.isArray(emojisArray),
                  originalEmojisType: typeof group.emojis
                }
              )
            }
          } else {
            console.warn(`[Content Storage] âŒ Group ${groupInfo.id} data is null/undefined`)
          }
        }
      }

      console.log(
        `[Content Storage] Processed ${groupIndex.length} groups, ${groups.length} valid groups found`
      )

      if (groups.length > 0) {
        console.log(
          `[Content Storage] Successfully loaded ${groups.length} groups from new storage system`
        )
        // Ensure favorites group is always first
        const favoritesGroup = groups.find(g => g.id === 'favorites')
        const otherGroups = groups
          .filter(g => g.id !== 'favorites')
          .sort((a, b) => a.order - b.order)
        return favoritesGroup ? [favoritesGroup, ...otherGroups] : otherGroups
      } else {
        console.warn(
          `[Content Storage] No valid groups found in new storage system despite having group index`
        )
      }
    }

    // Fallback to legacy emojiGroups key
    console.log('[Content Storage] Trying legacy emojiGroups key')
    const legacyGroups = await this.get('emojiGroups')
    if (legacyGroups && Array.isArray(legacyGroups) && legacyGroups.length > 0) {
      console.log(`[Content Storage] Loaded ${legacyGroups.length} groups from legacy storage`)
      // Ensure favorites group is always first for legacy data too
      const favoritesGroup = legacyGroups.find(g => g.id === 'favorites')
      const otherGroups = legacyGroups
        .filter(g => g.id !== 'favorites')
        .sort((a, b) => (a.order || 0) - (b.order || 0))
      return favoritesGroup ? [favoritesGroup, ...otherGroups] : otherGroups
    }

    console.log('[Content Storage] No groups found in storage')
    return []
  }

  async getSettings(): Promise<any> {
    console.log('[Content Storage] Getting settings')
    const settings = await this.get('appSettings')
    // Merge with central defaultSettings so fields like outputFormat are always present
    const result = settings ? { ...defaultSettings, ...settings } : { ...defaultSettings }
    console.log('[Content Storage] Settings loaded:', result)
    return result
  }
}

