      } else {
        toolbar.appendChild(uploadButton)
        toolbar.appendChild(emojiButton)
      }
    } else {
      // For standard toolbar, append at the end
      toolbar.appendChild(uploadButton)
      toolbar.appendChild(emojiButton)
    }
  } catch (e) {
    console.error('[Emoji Extension] Failed to inject buttons (module):', e)
  }
}

