/**
 * ä»Žå›¾ç‰‡URLä¸­æå–æ–‡ä»¶åä½œä¸ºé»˜è®¤åç§°ã€‚
 * @param url å›¾ç‰‡URLå­—ç¬¦ä¸²
 * @returns æå–çš„åç§°æˆ–é»˜è®¤å€¼ 'è¡¨æƒ…'
 */
function extractNameFromUrl(url: string): string {
  try {
    const urlObj = new URL(url)
    const pathname = urlObj.pathname
    const filename = pathname.split('/').pop() || ''

    // ç§»é™¤æ–‡ä»¶æ‰©å±•å
    const nameWithoutExt = filename.replace(/\.[^/.]+$/, '')

    // è§£ç URLç¼–ç 
    const decoded = decodeURIComponent(nameWithoutExt)

    // å¦‚æžœæ˜¯æ— æ„ä¹‰çš„å“ˆå¸Œå€¼æˆ–çŸ­å­—ç¬¦ä¸²ï¼Œè¿”å›žé»˜è®¤åç§°
    if (/^[0-9a-f]{8,}$/i.test(decoded) || decoded.length < 2) {
      return 'è¡¨æƒ…'
    }

    return decoded || 'è¡¨æƒ…'
  } catch {
    return 'è¡¨æƒ…'
  }
}

/**
 * ä»Žå›¾ç‰‡å…ƒç´ å’Œæ ‡é¢˜å®¹å™¨ä¸­æå–è¡¨æƒ…æ•°æ®ã€‚
 * @param imgElement å›¾ç‰‡å…ƒç´ 
 * @param titleContainer æ ‡é¢˜å®¹å™¨å…ƒç´ 
 * @returns åŒ…å«åç§°å’ŒURLçš„è¡¨æƒ…æ•°æ®å¯¹è±¡ï¼Œæˆ– null
 */
function extractEmojiDataFromMfp(
  imgElement: HTMLImageElement,
  titleContainer: Element
): AddEmojiButtonData | null {
  const src = imgElement.src
  if (!src || !src.startsWith('http')) {
    return null
  }

  // ä¼˜å…ˆä»Žtitleå®¹å™¨ä¸­èŽ·å–åç§°
  let name = ''
  const titleText = titleContainer.textContent || ''

  // è§£æžæ ‡é¢˜ï¼Œé€šå¸¸æ ¼å¼ä¸ºï¼š"æ–‡ä»¶å Â· å°ºå¯¸ å¤§å° Â· ä¸‹è½½ Â· åŽŸå§‹å›¾ç‰‡"
  const titleParts = titleText.split('Â·')
  if (titleParts.length > 0) {
    name = titleParts[0].trim()
  }

  // å¦‚æžœä»Žæ ‡é¢˜èŽ·å–ä¸åˆ°æœ‰æ•ˆåç§°ï¼Œå°è¯•ä½¿ç”¨ alt æˆ– title å±žæ€§ï¼Œæœ€åŽä½¿ç”¨URL
  if (!name || name.length < 2) {
    name = imgElement.alt || imgElement.title || extractNameFromUrl(src)
  }

  // æ¸…ç†åç§°
  name = name.trim()
  if (name.length === 0) {
    name = 'è¡¨æƒ…'
  }

  return {
    name,
    url: src
  }
}

/**
 * æ£€æŸ¥ä¸€ä¸ªå…ƒç´ æ˜¯å¦ä¸º Magnific Popup å›¾ç‰‡æŸ¥çœ‹å™¨å®¹å™¨ã€‚
