}

/**
 * ç›‘å¬ DOM å˜åŒ–ï¼Œå½“ Magnific Popup å‡ºçŽ°æ—¶è‡ªåŠ¨æ‰§è¡Œæ‰«æã€‚
 */
function observeMagnificPopup() {
  const observer = new MutationObserver(mutations => {
    let hasMfpChanges = false

    mutations.forEach(mutation => {
      if (mutation.type === 'childList') {
        // æ£€æŸ¥æ˜¯å¦æœ‰æ–°çš„èŠ‚ç‚¹è¢«æ·»åŠ ï¼Œå¹¶ä¸”æ˜¯ Magnific Popup ç›¸å…³çš„
        mutation.addedNodes.forEach(node => {
          if (node.nodeType === Node.ELEMENT_NODE) {
            const element = node as Element
            if (element.classList && element.classList.contains('mfp-wrap')) {
              hasMfpChanges = true
            }
          }
        })
      }
    })

    if (hasMfpChanges) {
      // çŸ­æš‚å»¶è¿Ÿä»¥ç¡®ä¿DOMå®Œå…¨æ›´æ–°ï¼Œç„¶åŽæ‰§è¡Œæ‰«æ
      setTimeout(scanForMagnificPopup, 100)
    }
  })

  // ç›‘å¬ body çš„å­èŠ‚ç‚¹å˜åŒ–ï¼ŒåŒ…æ‹¬å­æ ‘
  observer.observe(document.body, {
    childList: true,
    subtree: true
  })
}

/**
 * æ£€æŸ¥ä¸€ä¸ªå…ƒç´ æ˜¯å¦ä¸ºå¸¦æœ‰ cooked ç±»çš„å†…å®¹åŒºåŸŸã€‚
 * @param element è¦æ£€æŸ¥çš„å…ƒç´ 
 * @returns å¦‚æžœæ˜¯åˆ™è¿”å›ž true
