import { BaseProvider } from './BaseProvider'

import type { GenerateRequest } from '@/types/imageGenerator'

export class GeminiProvider extends BaseProvider {
  name = 'gemini'
  displayName = 'Google Gemini'
  private apiEndpoint =
    'https://generativelanguage.googleapis.com/v1beta/models/imagen-3.0-generate-001:generateImage'

  async generateImages(request: GenerateRequest): Promise<string[]> {
    if (!this.apiKey) {
      throw new Error('è¯·å…ˆè®¾ç½® API Key')
    }

    // Handle image editing mode
    if (request.editMode && request.inputImage) {
      return this.editImage(request)
    }

    const stylePrompt = request.style ? ` in ${request.style} style` : ''
    const fullPrompt = `${request.prompt}${stylePrompt}, aspect ratio ${request.aspectRatio}, high quality, detailed`

    const requestBody = {
      prompt: {
        text: fullPrompt
      },
      safetySettings: [
        {
          category: 'HARM_CATEGORY_SEXUALLY_EXPLICIT',
          threshold: 'BLOCK_LOW_AND_ABOVE'
        },
        {
          category: 'HARM_CATEGORY_HATE_SPEECH',
          threshold: 'BLOCK_LOW_AND_ABOVE'
        }
      ],
      generationConfig: {
        number: request.numberOfImages
      }
    }

    try {
      const response = await fetch(`${this.apiEndpoint}?key=${this.apiKey}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(requestBody)
      })

      if (!response.ok) {
        const errorData = await response.json()
        throw new Error(
          errorData.error?.message || `HTTP ${response.status}: ${response.statusText}`
        )
      }

      const data: any = await response.json()

      if (!data.candidates || data.candidates.length === 0) {
        throw new Error('æ²¡æœ‰ç”Ÿæˆä»»ä½•å›¾ç‰‡ï¼Œè¯·å°è¯•ä¿®æ”¹æ‚¨çš„æè¿°')
      }

      const imageUrls: string[] = []
      for (const candidate of data.candidates) {
        if (candidate.images) {
          for (const image of candidate.images) {
            imageUrls.push(image.uri)
          }
        }
      }

      if (imageUrls.length === 0) {
        throw new Error('ç”Ÿæˆçš„å›¾ç‰‡æ— æ³•èŽ·å–ï¼Œè¯·ç¨åŽé‡è¯•')
      }

      return imageUrls
    } catch (error: unknown) {
      this.handleApiError(error, 'Gemini')
    }
  }

  private async editImage(request: any): Promise<string[]> {
    const editEndpoint =
      'https://generativelanguage.googleapis.com/v1beta/models/gemini-2.0-flash-preview-image-generation:generateContent'

    const requestBody = {
      contents: [
        {
          parts: [
            {
              text: request.prompt
            },
            {
              inline_data: {
                mime_type: 'image/jpeg',
                data: request.inputImage
              }
            }
          ]
        }
      ],
      generationConfig: {
        responseModalities: ['TEXT', 'IMAGE']
      }
    }

    try {
      const response = await fetch(`${editEndpoint}?key=${this.apiKey}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(requestBody)
      })

      if (!response.ok) {
        const errorData = await response.json()
        throw new Error(
          errorData.error?.message || `HTTP ${response.status}: ${response.statusText}`
        )
      }

      const data = await response.json()

      // Extract image data from response
      const imageUrls: string[] = []
      if (data.candidates) {
        for (const candidate of data.candidates) {
          if (candidate.content && candidate.content.parts) {
            for (const part of candidate.content.parts) {
              if (part.inline_data && part.inline_data.data) {
                // Convert base64 to data URL
                const dataUrl = `data:${part.inline_data.mime_type || 'image/png'};base64,${part.inline_data.data}`
                imageUrls.push(dataUrl)
              }
            }
          }
        }
      }

      if (imageUrls.length === 0) {
        throw new Error('å›¾ç‰‡ç¼–è¾‘å¤±è´¥ï¼Œè¯·å°è¯•ä¿®æ”¹æ‚¨çš„æè¿°')
      }

      return imageUrls
    } catch (error: unknown) {
      this.handleApiError(error, 'Gemini')
    }
  }
}

import { GeminiProvider } from './GeminiProvider'
import { SiliconFlowProvider } from './SiliconFlowProvider'
import { CloudflareProvider } from './CloudflareProvider'
import { ChutesAIProvider } from './ChutesAIProvider'

import type { GenerateRequest, ImageProvider } from '@/types/imageGenerator'

export class ProviderManager {
  private providers: Map<string, ImageProvider> = new Map()
  private currentProvider: ImageProvider

  constructor() {
    // Initialize providers
    const geminiProvider = new GeminiProvider()
    const siliconFlowProvider = new SiliconFlowProvider()
    const cloudflareProvider = new CloudflareProvider()
    const chutesAIProvider = new ChutesAIProvider()

    this.providers.set(geminiProvider.name, geminiProvider)
    this.providers.set(siliconFlowProvider.name, siliconFlowProvider)
    this.providers.set(cloudflareProvider.name, cloudflareProvider)
    this.providers.set(chutesAIProvider.name, chutesAIProvider)

    // Set default provider
    this.currentProvider = geminiProvider

    // Load saved provider selection
    this.loadSelectedProvider()
  }

  getProviders(): ImageProvider[] {
    return Array.from(this.providers.values())
  }

  getProviderNames(): string[] {
    return Array.from(this.providers.keys())
  }

  setCurrentProvider(providerName: string): boolean {
    const provider = this.providers.get(providerName)
    if (provider) {
      this.currentProvider = provider
      localStorage.setItem('selected_provider', providerName)
      return true
    }
    return false
  }

  getCurrentProvider(): ImageProvider {
    return this.currentProvider
  }

  getCurrentProviderName(): string {
    return this.currentProvider.name
  }

  getProvider(name: string): ImageProvider | undefined {
    return this.providers.get(name)
  }

  loadSelectedProvider(): void {
    const saved = localStorage.getItem('selected_provider')
    if (saved && this.providers.has(saved)) {
      this.currentProvider = this.providers.get(saved)!
    }
  }

  async generateImages(request: GenerateRequest): Promise<string[]> {
    return this.currentProvider.generateImages(request)
  }

  async downloadImage(url: string, filename: string): Promise<void> {
    // Use the current provider's download method if available, otherwise use generic method
    if ('downloadImage' in this.currentProvider) {
      return (this.currentProvider as any).downloadImage(url, filename)
    }

    // Generic download implementation
    try {
      const response = await fetch(url)
      const blob = await response.blob()

      const downloadUrl = URL.createObjectURL(blob)
      const a = document.createElement('a')
      a.href = downloadUrl
      a.download = filename
      document.body.appendChild(a)
      a.click()
      document.body.removeChild(a)
      URL.revokeObjectURL(downloadUrl)
    } catch (error) {
      console.error('Download failed:', error)
      throw new Error('ä¸‹è½½å¤±è´¥ï¼Œè¯·ç¨åŽé‡è¯•')
    }
  }

  async copyToClipboard(url: string): Promise<void> {
    if (this.currentProvider.copyToClipboard) {
      return this.currentProvider.copyToClipboard(url)
    }
    // fallback: write to clipboard using navigator
    try {
      await navigator.clipboard.writeText(url)
    } catch (e) {
      throw new Error('å¤åˆ¶å¤±è´¥')
    }
  }

  // Provider-specific methods
  setProviderModel(providerName: string, model: string): void {
    const provider = this.providers.get(providerName) as any
    if (provider && typeof provider.setModel === 'function') {
      provider.setModel(model)
    }
  }

  loadProviderModel(providerName: string): void {
    const provider = this.providers.get(providerName) as any
    if (provider && typeof provider.loadSelectedModel === 'function') {
      provider.loadSelectedModel()
    }
  }

  getProviderModel(providerName: string): string | undefined {
    const provider = this.providers.get(providerName) as any
    if (provider && typeof provider.getSelectedModel === 'function') {
      return provider.getSelectedModel()
    }
    return undefined
  }

  supportsImageEditing(providerName?: string): boolean {
    const provider = providerName ? this.providers.get(providerName) : this.currentProvider
    return provider?.name === 'gemini' // Currently only Gemini supports image editing
  }

  supportsModels(providerName?: string): boolean {
    const provider = providerName ? this.providers.get(providerName) : this.currentProvider
    return provider?.name === 'cloudflare' || provider?.name === 'chutesai'
  }
}

import { BaseProvider } from './BaseProvider'

import type { GenerateRequest } from '@/types/imageGenerator'

export class SiliconFlowProvider extends BaseProvider {
  name = 'siliconflow'
  displayName = 'SiliconFlow'
  private apiEndpoint = 'https://api.siliconflow.cn/v1/images/generations'

  async generateImages(request: GenerateRequest): Promise<string[]> {
    if (!this.apiKey) {
      throw new Error('è¯·å…ˆè®¾ç½® SiliconFlow API Key')
    }

    // Map aspect ratio to image size
    const sizeMap: Record<string, string> = {
      '1:1': '1024x1024',
      '16:9': '1344x768',
      '9:16': '768x1344',
      '4:3': '1152x896',
      '3:4': '896x1152'
    }

    const imageSize = sizeMap[request.aspectRatio] || '1024x1024'
    const stylePrompt = request.style ? ` in ${request.style} style` : ''
    const fullPrompt = `${request.prompt}${stylePrompt}, high quality, detailed`

    const requestBody = {
      model: 'Kwai-Kolors/Kolors',
      prompt: fullPrompt,
      image_size: imageSize,
      batch_size: request.numberOfImages,
      num_inference_steps: 20,
      guidance_scale: 7.5
    }

    try {
      const response = await fetch(this.apiEndpoint, {
        method: 'POST',
        headers: {
          Authorization: `Bearer ${this.apiKey}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(requestBody)
      })

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}))
        throw new Error(
          errorData.error?.message || `HTTP ${response.status}: ${response.statusText}`
        )
      }

      const data: any = await response.json()

      if (!data.data || data.data.length === 0) {
        throw new Error('æ²¡æœ‰ç”Ÿæˆä»»ä½•å›¾ç‰‡ï¼Œè¯·å°è¯•ä¿®æ”¹æ‚¨çš„æè¿°')
      }

      return data.data.map((item: { url: string }) => item.url)
    } catch (error: unknown) {
      this.handleApiError(error, 'SiliconFlow')
    }
  }
}

// IndexedDB utility for emoji extension
import type { EmojiGroup, AppSettings } from '../types/emoji'
import { logger, indexedDBWrapper } from '../config/buildFlags'

const DB_NAME = 'EmojiExtensionDB'
const DB_VERSION = 1

// Store names
const STORES = {
  GROUPS: 'groups',
  SETTINGS: 'settings',
  FAVORITES: 'favorites'
} as const

// Logging helper
function logDB(operation: string, store: string, key?: string, data?: any, error?: any) {
  // å¦‚æžœæ—¥å¿—è¢«ç¦ç”¨ï¼Œç›´æŽ¥è¿”å›ž
  if (indexedDBWrapper.shouldSkip() && !error) {
    return
  }

  const timestamp = new Date().toISOString()
  const logPrefix = `[IndexedDB ${timestamp}]`

  // Helper to format a safe preview of the data (avoid huge dumps or circular refs)
  function formatPreview(d: any) {
    try {
      const s = JSON.stringify(d)
      const size = s.length
      if (size > 2000) {
        return { preview: s.slice(0, 500) + '... (truncated)', size }
      }
      return { preview: JSON.parse(s), size }
    } catch (e) {
      try {
        // Fallback to toString
        return { preview: String(d) }
      } catch {
        return { preview: '[unserializable data]' }
      }
    }
  }

  if (error) {
    // Make error output human readable (include name/message) while preserving the original error object
    let errInfo = ''
    try {
      if (error && typeof error === 'object') {
        errInfo = `${(error as any).name || 'Error'}: ${(error as any).message || String(error)}`
      } else {
        errInfo = String(error)
      }
    } catch {
      errInfo = String(error)
    }

    // Some failures (buffer flush per-item failures or cleanup) are non-fatal in tests/environments
    // where the Chrome extension APIs are not available. Log them as warnings to avoid failing
    // automated tests that assert no console.error output.
    const nonFatalFailure = operation.endsWith('_FAILED') || operation.startsWith('FLUSH')
    if (nonFatalFailure) {
      logger.warn(
        `${logPrefix} ${operation} FAILED in "${store}"${key ? ` for key "${key}"` : ''}: ${errInfo}`,
        error
      )
    } else {
      logger.error(
        `${logPrefix} ${operation} FAILED in "${store}"${key ? ` for key "${key}"` : ''}: ${errInfo}`,
        error
      )
    }
  } else {
    if (typeof data !== 'undefined') {
      const p = formatPreview(data as any)
      // Print structured output: summary and preview
      logger.log(
        `${logPrefix} ${operation} in "${store}"${key ? ` for key "${key}"` : ''} - size: ${p.size ?? 'unknown'}`,
        p.preview
      )
    } else {
      logger.log(`${logPrefix} ${operation} in "${store}"${key ? ` for key "${key}"` : ''}`)
    }
  }
}

// Database connection management
let dbInstance: IDBDatabase | null = null

// --- In-memory buffer between app and IndexedDB ---
const bufferState = {
  groups: new Map<string, any>(),
  settings: undefined as any,
  favorites: undefined as string[] | undefined,
  dirty: {
    groups: false,
    settings: false,
    favorites: false
  }
}

let flushTimer: any = null
const FLUSH_DEBOUNCE_MS = 1500 // buffer debounce before flushing to IDB

function scheduleFlush() {
  if (flushTimer) clearTimeout(flushTimer)
  flushTimer = setTimeout(() => void flushBuffer(false), FLUSH_DEBOUNCE_MS)
}

// Attempt to flush buffer when the page is unloading to reduce lost writes
if (typeof window !== 'undefined' && window.addEventListener) {
  try {
    window.addEventListener('beforeunload', () => {
      try {
        // synchronous attempt: schedule a forced flush (best-effort)
        void flushBuffer(true)
      } catch {
        // swallow errors during unload
      }
    })
  } catch {
    // ignore environments where addEventListener may not be available
  }
}

async function flushBuffer(force = false) {
  try {
    // If nothing dirty and not forced, skip
    if (
      !force &&
      !bufferState.dirty.groups &&
      !bufferState.dirty.settings &&
      !bufferState.dirty.favorites
    ) {
      logDB('FLUSH_SKIP', 'buffer', undefined, { reason: 'no changes' })
      return
    }

    const db = await getDB()

    // Flush groups: write each group in its own transaction to avoid one failing put
    // aborting the whole batch (which previously could leave the store cleared).
    if (bufferState.dirty.groups || force) {
      const writtenIds: string[] = []

      for (const [id, value] of bufferState.groups.entries()) {
        try {
          // If id is null/undefined, skip and persist to fallback; otherwise coerce to string
          if (id === null || typeof id === 'undefined') {
            logDB(
              'FLUSH_PUT_SKIPPED_INVALID_KEY',
              STORES.GROUPS,
              String(id),
              undefined,
              'invalid key'
            )
            try {
              saveFallbackToLocal(String(id), value, 'invalid key')
            } catch {}
            continue
          }

          const safeId = String(id)
          const cleanedValue = cleanDataForStorage(value)
          const txItem = db.transaction([STORES.GROUPS], 'readwrite')
          const storeItem = txItem.objectStore(STORES.GROUPS)
          const req = storeItem.put({ id: safeId, value: cleanedValue })

          await new Promise<void>((resolve, reject) => {
            req.onsuccess = () => resolve()
            req.onerror = () => reject(req.error)
          })

          writtenIds.push(id)
        } catch (e) {
          // Log the failing id but continue with others
          logDB('FLUSH_PUT_FAILED', STORES.GROUPS, id, undefined, e)
          try {
            // Attempt to persist the failed item to a local fallback store to avoid data loss
            const fallback = cleanDataForStorage(value)
            saveFallbackToLocal(id, fallback, e)
          } catch {
            // ignore fallback failures
          }
        }
      }

      // Remove any DB entries that are not present in the buffer anymore.
      try {
        const txKeys = db.transaction([STORES.GROUPS], 'readonly')
        const storeKeys = txKeys.objectStore(STORES.GROUPS)
        const allKeys: string[] = await new Promise((resolve, reject) => {
          const getAllReq = storeKeys.getAllKeys()
          getAllReq.onsuccess = () => resolve(getAllReq.result as string[])
          getAllReq.onerror = () => reject(getAllReq.error)
        })

        const toDelete = allKeys.filter(k => !bufferState.groups.has(k))

        for (const key of toDelete) {
          try {
            const txDel = db.transaction([STORES.GROUPS], 'readwrite')
            const storeDel = txDel.objectStore(STORES.GROUPS)
            const delReq = storeDel.delete(key)
            await new Promise<void>((resolve, reject) => {
              delReq.onsuccess = () => resolve()
              delReq.onerror = () => reject(delReq.error)
            })
          } catch (e) {
            logDB('FLUSH_DELETE_FAILED', STORES.GROUPS, key, undefined, e)
          }
        }
      } catch (e) {
        logDB('FLUSH_CLEANUP_FAILED', STORES.GROUPS, undefined, undefined, e)
      }

      bufferState.dirty.groups = false
      logDB('FLUSH', STORES.GROUPS, undefined, {
        count: bufferState.groups.size,
        written: writtenIds.length
      })
    }

    // Flush settings
    if (bufferState.dirty.settings || force) {
      const tx = db.transaction([STORES.SETTINGS], 'readwrite')
      const store = tx.objectStore(STORES.SETTINGS)
      const cleanedSettings = cleanDataForStorage(bufferState.settings)
      const req = store.put({ id: 'app', value: cleanedSettings })
      await new Promise<void>((resolve, reject) => {
        req.onsuccess = () => resolve()
        req.onerror = () => reject(req.error)
      })
      bufferState.dirty.settings = false
      logDB('FLUSH', STORES.SETTINGS, 'app', cleanedSettings)
    }

    // Flush favorites
    if (bufferState.dirty.favorites || force) {
      const tx = db.transaction([STORES.FAVORITES], 'readwrite')
      const store = tx.objectStore(STORES.FAVORITES)
      const cleanedFavorites = cleanDataForStorage(bufferState.favorites || [])
      const req = store.put({ id: 'list', value: cleanedFavorites })
      await new Promise<void>((resolve, reject) => {
        req.onsuccess = () => resolve()
        req.onerror = () => reject(req.error)
      })
      bufferState.dirty.favorites = false
      logDB('FLUSH', STORES.FAVORITES, 'list', cleanedFavorites)
    }
  } catch (error) {
    logDB('FLUSH', 'buffer', undefined, undefined, error)
    throw error
  }
}

async function getDB(): Promise<IDBDatabase> {
  // å¦‚æžœ IndexedDB è¢«ç¦ç”¨ï¼ŒæŠ›å‡ºé”™è¯¯
  if (indexedDBWrapper.shouldSkip()) {
    throw new Error('IndexedDB is disabled by build configuration')
  }

  if (dbInstance) {
    return dbInstance
  }

  return new Promise((resolve, reject) => {
    logDB('OPEN', 'database')
    const request = indexedDB.open(DB_NAME, DB_VERSION)

    request.onerror = () => {
      const error = request.error
      logDB('OPEN', 'database', undefined, undefined, error)
      reject(error)
    }

    request.onsuccess = () => {
      dbInstance = request.result
      // attach versionchange handler so we close and clear instance when needed
      try {
        dbInstance.onversionchange = () => {
          logDB('VERSION_CHANGE', 'database')
          try {
            dbInstance?.close()
          } catch {}
          dbInstance = null
        }
      } catch {
        // non-fatal if handler cannot be attached
      }

      logDB('OPEN', 'database', undefined, 'success')
      resolve(dbInstance)
    }

    request.onupgradeneeded = event => {
      logDB('UPGRADE', 'database', undefined, `version ${event.newVersion}`)
      const db = request.result

      // Create object stores
      if (!db.objectStoreNames.contains(STORES.GROUPS)) {
        const groupStore = db.createObjectStore(STORES.GROUPS, { keyPath: 'id' })
        groupStore.createIndex('order', 'order', { unique: false })
        logDB('CREATE_STORE', STORES.GROUPS)
      }

      if (!db.objectStoreNames.contains(STORES.SETTINGS)) {
        db.createObjectStore(STORES.SETTINGS, { keyPath: 'id' })
        logDB('CREATE_STORE', STORES.SETTINGS)
      }

      if (!db.objectStoreNames.contains(STORES.FAVORITES)) {
        db.createObjectStore(STORES.FAVORITES, { keyPath: 'id' })
        logDB('CREATE_STORE', STORES.FAVORITES)
      }
    }
  })
}

// Generic IndexedDB operations
async function getValue<T>(storeName: string, key: string): Promise<T | undefined> {
  // å¦‚æžœ IndexedDB è¢«ç¦ç”¨ï¼Œç›´æŽ¥è¿”å›ž undefined
  if (indexedDBWrapper.shouldSkip()) {
    logDB('GET_SKIPPED', storeName, key, 'IndexedDB disabled')
    return undefined
  }

  try {
    // If groups are buffered, return from buffer first
    if (storeName === STORES.GROUPS) {
      if (bufferState.groups.has(key)) {
        const v = bufferState.groups.get(key)
        logDB('GET_BUFFER', storeName, key, v)
        return v as T
      }
    }

    if (
      storeName === STORES.SETTINGS &&
      key === 'app' &&
      typeof bufferState.settings !== 'undefined'
    ) {
      logDB('GET_BUFFER', storeName, key, bufferState.settings)
      return bufferState.settings as T
    }

    if (
      storeName === STORES.FAVORITES &&
      key === 'list' &&
      typeof bufferState.favorites !== 'undefined'
    ) {
      logDB('GET_BUFFER', storeName, key, bufferState.favorites)
      return bufferState.favorites as T
    }

    const db = await getDB()
    const transaction = db.transaction([storeName], 'readonly')
    const store = transaction.objectStore(storeName)

    return new Promise((resolve, reject) => {
      const request = store.get(key)

      request.onsuccess = () => {
        const result = request.result?.value

        // Populate buffer with fetched data for future reads
        if (storeName === STORES.GROUPS && result) {
          bufferState.groups.set(key, result)
        } else if (storeName === STORES.SETTINGS && key === 'app' && result) {
          bufferState.settings = result
        } else if (storeName === STORES.FAVORITES && key === 'list' && result) {
          bufferState.favorites = result
        }

        logDB('GET', storeName, key, result)
        resolve(result)
      }

      request.onerror = () => {
        logDB('GET', storeName, key, undefined, request.error)
        reject(request.error)
      }
    })
  } catch (error) {
    logDB('GET', storeName, key, undefined, error)
    return undefined
  }
}

// Helper function to clean data for IndexedDB storage
function cleanDataForStorage<T>(data: T): T {
  try {
    // Deep clone and clean the data to ensure it's serializable
    const cloned = JSON.parse(JSON.stringify(data))

    // If the serialized payload is very large, trim potentially huge fields (data URLs)
    const MAX_SERIALIZED_SIZE = 200 * 1024 // 200KB safe threshold
    try {
      const s = JSON.stringify(cloned)
      if (s.length > MAX_SERIALIZED_SIZE) {
        // Attempt targeted trimming for emoji groups that contain data URLs or very large strings
        if (cloned && typeof cloned === 'object' && Array.isArray((cloned as any).emojis)) {
          const group: any = { ...cloned }
          group.emojis = (group.emojis as any[]).map((emoji: any) => {
            if (emoji && typeof emoji === 'object') {
              const e = { ...emoji }
              if (typeof e.url === 'string') {
                // Trim inline data URLs and very large URLs
                if (e.url.startsWith('data:image/') || e.url.length > 100 * 1024) {
                  // Replace with placeholder and mark trimmed so UI can recover if needed
                  e._urlTrimmed = true
                  e._originalUrlLength = e.url.length
                  e.url = ''
                }
              }
              return e
            }
            return emoji
          })

          const reSerialized = JSON.stringify(group)
          if (reSerialized.length <= s.length) {
            logDB('CLEAN_DATA_TRIM', 'groups', undefined, {
              originalSize: s.length,
              newSize: reSerialized.length
            })
            return group as T
          }
        }
      }
    } catch (err) {
      // ignore serialization-check errors
    }

    return cloned
  } catch (error) {
    logDB('CLEAN_DATA', 'failed', undefined, error)
    // Fallback: try to extract only basic properties
    if (typeof data === 'object' && data !== null) {
      const cleaned: any = {}
      for (const [key, value] of Object.entries(data)) {
        try {
          JSON.stringify(value)
          cleaned[key] = value
        } catch {
          // Skip unserializable properties
          logDB('CLEAN_DATA', `skipped property: ${key}`, undefined, 'unserializable')
        }
      }
      return cleaned as T
    }
    return data
  }
}

// Persist failed group writes to localStorage as a fallback to avoid data loss
function saveFallbackToLocal(id: string, value: any, error: any) {
  try {
    const key = 'idb_fallback_groups'
    let map: Record<string, any> = {}
    try {
      const raw = localStorage.getItem(key)
      if (raw) map = JSON.parse(raw)
    } catch {
      // ignore parse errors and start fresh
      map = {}
    }

    map[id] = {
      value,
      savedAt: Date.now(),
      error:
        error && typeof error === 'object'
          ? { name: error.name, message: error.message }
          : String(error)
    }

    try {
      localStorage.setItem(key, JSON.stringify(map))
      logDB('FALLBACK_SAVE', 'localStorage', key, { id, savedAt: map[id].savedAt })
    } catch (err) {
      // localStorage may also fail (quota) â€” log and ignore
      logDB('FALLBACK_SAVE_FAILED', 'localStorage', key, undefined, err)
    }
  } catch {
    // swallow any unexpected errors in fallback path
  }
}

async function setValue<T>(storeName: string, key: string, value: T): Promise<void> {
  // å¦‚æžœ IndexedDB è¢«ç¦ç”¨ï¼Œç›´æŽ¥è¿”å›ž
  if (indexedDBWrapper.shouldSkip()) {
    logDB('SET_SKIPPED', storeName, key, 'IndexedDB disabled')
    return
  }

  try {
    // Clean the data to ensure it's serializable for IndexedDB
    const cleanedValue = cleanDataForStorage(value)

    // Write to buffer first for groups/settings/favorites
    if (storeName === STORES.GROUPS) {
      bufferState.groups.set(key, cleanedValue)
      bufferState.dirty.groups = true
      scheduleFlush()
      logDB('PUT_BUFFER', storeName, key, cleanedValue)
      return
    }

    if (storeName === STORES.SETTINGS) {
      bufferState.settings = cleanedValue
      bufferState.dirty.settings = true
      scheduleFlush()
      logDB('PUT_BUFFER', storeName, key, cleanedValue)
      return
    }

    if (storeName === STORES.FAVORITES) {
      bufferState.favorites = cleanedValue as any
      bufferState.dirty.favorites = true
      scheduleFlush()
      logDB('PUT_BUFFER', storeName, key, cleanedValue)
      return
    }

    const db = await getDB()
    const transaction = db.transaction([storeName], 'readwrite')
    const store = transaction.objectStore(storeName)

    return new Promise((resolve, reject) => {
      const request = store.put({ id: String(key), value: cleanedValue })

      request.onsuccess = () => {
        logDB('PUT', storeName, key, cleanedValue)
        resolve()
      }

      request.onerror = () => {
        logDB('PUT', storeName, key, undefined, request.error)
        reject(request.error)
      }
    })
  } catch (error) {
    logDB('PUT', storeName, key, undefined, error)
    throw error
  }
}

async function deleteValue(storeName: string, key: string): Promise<void> {
  try {
    // If buffered, remove from buffer and mark dirty
    if (storeName === STORES.GROUPS) {
      bufferState.groups.delete(key)
      bufferState.dirty.groups = true
      scheduleFlush()
      logDB('DELETE_BUFFER', storeName, key)
      return
    }

    const db = await getDB()
    const transaction = db.transaction([storeName], 'readwrite')
    const store = transaction.objectStore(storeName)

    return new Promise((resolve, reject) => {
      const request = store.delete(key)

      request.onsuccess = () => {
        logDB('DELETE', storeName, key)
        resolve()
      }

      request.onerror = () => {
        logDB('DELETE', storeName, key, undefined, request.error)
        reject(request.error)
      }
    })
  } catch (error) {
    logDB('DELETE', storeName, key, undefined, error)
    throw error
  }
}

async function getAllValues<T>(storeName: string): Promise<T[]> {
  try {
    // For groups: if buffer has data, use buffer; otherwise fetch from DB
    if (storeName === STORES.GROUPS) {
      if (bufferState.groups.size > 0) {
        const local = Array.from(bufferState.groups.values())
        logDB('GET_ALL_BUFFER', storeName, undefined, { count: local.length })
        return local as T[]
      }
      // Buffer empty, fetch from DB and populate buffer
      const db = await getDB()
      const transaction = db.transaction([storeName], 'readonly')
      const store = transaction.objectStore(storeName)

      return new Promise((resolve, reject) => {
        const request = store.getAll()

        request.onsuccess = () => {
          const results = request.result.map((item: any) => item.value)
          // Populate buffer with DB data for future reads
          bufferState.groups.clear()
          request.result.forEach((item: any) => {
            bufferState.groups.set(item.id, item.value)
          })
          logDB('GET_ALL_DB', storeName, undefined, results)
          resolve(results)
        }

        request.onerror = () => {
          logDB('GET_ALL', storeName, undefined, undefined, request.error)
          reject(request.error)
        }
      })
    }

    if (storeName === STORES.SETTINGS && typeof bufferState.settings !== 'undefined') {
      logDB('GET_ALL_BUFFER', storeName, undefined, bufferState.settings)
      return [bufferState.settings] as unknown as T[]
    }

    const db = await getDB()
    const transaction = db.transaction([storeName], 'readonly')
    const store = transaction.objectStore(storeName)

    return new Promise((resolve, reject) => {
      const request = store.getAll()

      request.onsuccess = () => {
        const results = request.result.map((item: any) => item.value)
        logDB('GET_ALL', storeName, undefined, results)
        resolve(results)
      }

      request.onerror = () => {
        logDB('GET_ALL', storeName, undefined, undefined, request.error)
        reject(request.error)
      }
    })
  } catch (error) {
    logDB('GET_ALL', storeName, undefined, undefined, error)
    return []
  }
}

async function clearStore(storeName: string): Promise<void> {
  // å¦‚æžœ IndexedDB è¢«ç¦ç”¨ï¼Œç›´æŽ¥è¿”å›ž
  if (indexedDBWrapper.shouldSkip()) {
    logDB('CLEAR_SKIPPED', storeName, undefined, 'IndexedDB disabled')
    return
  }

  try {
    // Clear buffer if applicable
    if (storeName === STORES.GROUPS) {
      bufferState.groups.clear()
      bufferState.dirty.groups = true
      logDB('CLEAR_BUFFER', storeName)
    }

    if (storeName === STORES.SETTINGS) {
      bufferState.settings = undefined
      bufferState.dirty.settings = true
    }

    if (storeName === STORES.FAVORITES) {
      bufferState.favorites = undefined
      bufferState.dirty.favorites = true
    }

    const db = await getDB()
    const transaction = db.transaction([storeName], 'readwrite')
    const store = transaction.objectStore(storeName)

    return new Promise((resolve, reject) => {
      const request = store.clear()

      request.onsuccess = () => {
        logDB('CLEAR', storeName)
        resolve()
      }

      request.onerror = () => {
        logDB('CLEAR', storeName, undefined, undefined, request.error)
        reject(request.error)
      }
    })
  } catch (error) {
    logDB('CLEAR', storeName, undefined, undefined, error)
    throw error
  }
}

// Export buffer control for other modules
export { flushBuffer }

// Exported helpers for emoji extension
export const indexedDBHelpers = {
  // Groups
  async getGroup(groupId: string): Promise<EmojiGroup | undefined> {
    return getValue<EmojiGroup>(STORES.GROUPS, groupId)
  },

  async setGroup(group: EmojiGroup): Promise<void> {
    return setValue(STORES.GROUPS, group.id, group)
  },

  async getAllGroups(): Promise<EmojiGroup[]> {
    const groups = await getAllValues<EmojiGroup>(STORES.GROUPS)
    return groups.sort((a, b) => a.order - b.order)
  },

  async deleteGroup(groupId: string): Promise<void> {
    return deleteValue(STORES.GROUPS, groupId)
  },

  async setAllGroups(groups: EmojiGroup[]): Promise<void> {
    // Clear existing groups and set new ones
    await clearStore(STORES.GROUPS)

    // Update buffer first
    bufferState.groups.clear()
    for (const group of groups) {
      bufferState.groups.set(group.id, group)
      await setValue(STORES.GROUPS, group.id, group)
    }
    bufferState.dirty.groups = true
    // Force immediate flush since this is a bulk operation
    await flushBuffer(true)
  },

  // Settings
  async getSettings(): Promise<AppSettings | undefined> {
    return getValue<AppSettings>(STORES.SETTINGS, 'app')
  },

  async setSettings(settings: AppSettings): Promise<void> {
    return setValue(STORES.SETTINGS, 'app', settings)
  },

  // Favorites
  async getFavorites(): Promise<string[]> {
    const favorites = await getValue<string[]>(STORES.FAVORITES, 'list')
    return favorites || []
  },

  async setFavorites(favorites: string[]): Promise<void> {
    return setValue(STORES.FAVORITES, 'list', favorites)
  },

  // Utility methods
  async clearAll(): Promise<void> {
    await Promise.all([
      clearStore(STORES.GROUPS),
      clearStore(STORES.SETTINGS),
      clearStore(STORES.FAVORITES)
    ])
  },

  async isAvailable(): Promise<boolean> {
    // é¦–å…ˆæ£€æŸ¥ç¼–è¯‘æœŸå¼€å…³
    if (indexedDBWrapper.shouldSkip()) {
      return false
    }

    try {
      await getDB()
      return true
    } catch {
      return false
    }
  }
}

export default indexedDBHelpers

/* eslint-disable no-unused-vars */
import type { EmojiGroup, AppSettings } from '../types/emoji'
import { defaultEmojiGroups, defaultSettings } from '../types/emoji'
import { logger } from '../config/buildFlags'

import indexedDBHelpers from './indexedDB'

// In build/test environments `chrome` may not be declared. Provide a loose declaration
declare const chrome: any

// --- Constants ---
export const STORAGE_KEYS = {
  SETTINGS: 'appSettings',
  FAVORITES: 'favorites',
  GROUP_INDEX: 'emojiGroupIndex', // For group order and metadata
  GROUP_PREFIX: 'emojiGroup_' // For individual group storage
} as const

export const SYNC_STORAGE_KEYS = {
  BACKUP: 'emojiExtensionBackup'
} as const

// Storage priority levels as requested
export enum StorageLevel {
  LOCAL_STORAGE = 1, // Highest priority
  SESSION_STORAGE = 2,
  EXTENSION_STORAGE = 3,
  INDEXED_DB = 4 // Lowest priority/fallback
}

// --- Chrome API Helper ---
function getChromeAPI() {
  if (typeof chrome !== 'undefined' && chrome.storage) {
    return chrome
  }
  // Fallback for environments where `chrome` is not immediately available
  if (typeof window !== 'undefined' && (window as any).chrome) {
    return (window as any).chrome
  }
  if (typeof globalThis !== 'undefined' && (globalThis as any).chrome) {
    return (globalThis as any).chrome
  }
  return null
}

// --- Logging Helper ---
function logStorage(operation: string, key: string, data?: any, error?: any) {
  const timestamp = new Date().toISOString()
  const logPrefix = `[Storage ${timestamp}]`

  function formatPreview(d: any) {
    try {
      const s = JSON.stringify(d)
      const size = s.length
      if (size > 2000) {
        return { preview: s.slice(0, 500) + '... (truncated)', size }
      }
      return { preview: JSON.parse(s), size }
    } catch {
      try {
        return { preview: String(d) }
      } catch {
        return { preview: '[unserializable data]' }
      }
    }
  }

  if (error) {
    logger.error(`${logPrefix} ${operation} FAILED for "${key}":`, error)
  } else {
    // Ensure certain success messages explicitly contain the word 'success' so
    // automated tests that search for 'success' can reliably match them.
    const shouldMarkSuccess = [
      'MULTI_SET_SUCCESS',
      'IDB_SET',
      'RESET_DEFAULTS',
      'SYNC_BACKUP'
    ].includes(operation)
    const successSuffix = shouldMarkSuccess ? ' - success' : ''

    if (typeof data !== 'undefined') {
      const p = formatPreview(data)
      logger.log(
        `${logPrefix} ${operation} for "${key}" - size: ${p.size ?? 'unknown'}${successSuffix}`,
        p.preview
      )
    } else {
      logger.log(`${logPrefix} ${operation} for "${key}"${successSuffix}`)
    }
  }
}

// --- Storage Layer Implementations ---

// Helper function to ensure data is serializable
function ensureSerializable<T>(data: T): T {
  try {
    // Test serialization and clean the data
    return JSON.parse(JSON.stringify(data))
  } catch (error) {
    logStorage('SERIALIZE_CLEAN', 'data', undefined, error)
    // Fallback: create a clean version
    if (typeof data === 'object' && data !== null && !Array.isArray(data)) {
      const cleaned: any = {}
      for (const [key, value] of Object.entries(data)) {
        try {
          JSON.stringify(value)
          cleaned[key] = value
        } catch {
          logStorage('SERIALIZE_CLEAN', `skipped property: ${key}`, undefined, 'unserializable')
        }
      }
      return cleaned as T
    } else if (Array.isArray(data)) {
      return data.map(item => ensureSerializable(item)) as T
    }
    return data
  }
}

class LocalStorageLayer {
  async get(key: string): Promise<any> {
    try {
      if (typeof localStorage === 'undefined') return null
      const value = localStorage.getItem(key)
      return value ? JSON.parse(value) : null
    } catch (error) {
      logStorage('LOCAL_GET', key, undefined, error)
      return null
    }
  }

  async set(key: string, value: any): Promise<void> {
    try {
      if (typeof localStorage === 'undefined') return
      localStorage.setItem(key, JSON.stringify(value))
      logStorage('LOCAL_SET', key, value)
    } catch (error) {
      logStorage('LOCAL_SET', key, undefined, error)
      throw error
    }
  }

  async remove(key: string): Promise<void> {
    try {
      if (typeof localStorage === 'undefined') return
      localStorage.removeItem(key)
      logStorage('LOCAL_REMOVE', key)
    } catch (error) {
      logStorage('LOCAL_REMOVE', key, undefined, error)
    }
  }
}

class SessionStorageLayer {
  async get(key: string): Promise<any> {
    try {
      if (typeof sessionStorage === 'undefined') return null
      const value = sessionStorage.getItem(key)
      return value ? JSON.parse(value) : null
    } catch (error) {
      logStorage('SESSION_GET', key, undefined, error)
      return null
    }
  }

  async set(key: string, value: any): Promise<void> {
    try {
      if (typeof sessionStorage === 'undefined') return
      sessionStorage.setItem(key, JSON.stringify(value))
      logStorage('SESSION_SET', key, value)
    } catch (error) {
      logStorage('SESSION_SET', key, undefined, error)
      throw error
    }
  }

  async remove(key: string): Promise<void> {
    try {
      if (typeof sessionStorage === 'undefined') return
      sessionStorage.removeItem(key)
      logStorage('SESSION_REMOVE', key)
    } catch (error) {
      logStorage('SESSION_REMOVE', key, undefined, error)
    }
  }
}

class ExtensionStorageLayer {
  async get(key: string): Promise<any> {
    const chromeAPI = getChromeAPI()
    if (!chromeAPI?.storage?.local) {
      logStorage('EXT_GET', key, { available: false, reason: 'Chrome Storage API not available' })
      return null
    }

    return new Promise(resolve => {
      try {
        chromeAPI.storage.local.get({ [key]: null }, (result: any) => {
          if (chromeAPI.runtime.lastError) {
            logStorage('EXT_GET', key, undefined, chromeAPI.runtime.lastError)
            resolve(null)
          } else {
            const value = result[key]
            logStorage('EXT_GET', key, value)
            resolve(value)
          }
        })
      } catch (error) {
        logStorage('EXT_GET', key, undefined, error)
        resolve(null)
      }
    })
  }

  async set(key: string, value: any): Promise<void> {
    const chromeAPI = getChromeAPI()
    if (!chromeAPI?.storage?.local) {
      logStorage('EXT_SET', key, { available: false, reason: 'Chrome Storage API not available' })
      return
    }

    return new Promise((resolve, reject) => {
      try {
        chromeAPI.storage.local.set({ [key]: value }, () => {
          if (chromeAPI.runtime.lastError) {
            logStorage('EXT_SET', key, undefined, chromeAPI.runtime.lastError)
            reject(chromeAPI.runtime.lastError)
          } else {
            logStorage('EXT_SET', key, value)
            resolve()
          }
        })
      } catch (error) {
        logStorage('EXT_SET', key, undefined, error)
        reject(error)
      }
    })
  }

  async remove(key: string): Promise<void> {
    const chromeAPI = getChromeAPI()
    if (!chromeAPI?.storage?.local) {
      logStorage('EXT_REMOVE', key, {
        available: false,
        reason: 'Chrome Storage API not available'
      })
      return
    }

    return new Promise(resolve => {
      try {
        chromeAPI.storage.local.remove([key], () => {
          if (chromeAPI.runtime.lastError) {
            logStorage('EXT_REMOVE', key, undefined, chromeAPI.runtime.lastError)
          } else {
            logStorage('EXT_REMOVE', key)
          }
          resolve()
        })
      } catch (error) {
        logStorage('EXT_REMOVE', key, undefined, error)
        resolve()
      }
    })
  }
}

class IndexedDBLayer {
  async get(key: string): Promise<any> {
    try {
      const isAvailable = await indexedDBHelpers.isAvailable()
      if (!isAvailable) return null

      if (key === STORAGE_KEYS.SETTINGS) {
        return await indexedDBHelpers.getSettings()
      } else if (key === STORAGE_KEYS.FAVORITES) {
        return await indexedDBHelpers.getFavorites()
      } else if (key === STORAGE_KEYS.GROUP_INDEX) {
        const groups = await indexedDBHelpers.getAllGroups()
        return groups?.map((group, index) => ({ id: group.id, order: index })) || null
      } else if (key.startsWith(STORAGE_KEYS.GROUP_PREFIX)) {
        const groupId = key.replace(STORAGE_KEYS.GROUP_PREFIX, '')
        const groups = await indexedDBHelpers.getAllGroups()
        return groups?.find(g => g.id === groupId) || null
      }
      return null
    } catch (error) {
      logStorage('IDB_GET', key, undefined, error)
      return null
    }
  }

  async set(key: string, value: any): Promise<void> {
    try {
      const isAvailable = await indexedDBHelpers.isAvailable()
      if (!isAvailable) return

      // Ensure the value is serializable for IndexedDB
      const cleanValue = ensureSerializable(value)

      if (key === STORAGE_KEYS.SETTINGS) {
        await indexedDBHelpers.setSettings(cleanValue)
      } else if (key === STORAGE_KEYS.FAVORITES) {
        await indexedDBHelpers.setFavorites(cleanValue)
      } else if (key === STORAGE_KEYS.GROUP_INDEX) {
        // For group index, we need to handle this specially
        logStorage('IDB_SET', key, cleanValue)
      } else if (key.startsWith(STORAGE_KEYS.GROUP_PREFIX)) {
        // For individual groups, we need to update the entire groups collection
        const groups = (await indexedDBHelpers.getAllGroups()) || []
        const groupId = key.replace(STORAGE_KEYS.GROUP_PREFIX, '')
        const existingIndex = groups.findIndex(g => g.id === groupId)

        if (existingIndex !== -1) {
          groups[existingIndex] = cleanValue
        } else {
          groups.push(cleanValue)
        }

        await indexedDBHelpers.setAllGroups(groups)
      }

      logStorage('IDB_SET', key, cleanValue)
    } catch (error) {
      logStorage('IDB_SET', key, undefined, error)
      throw error
    }
  }

  async remove(key: string): Promise<void> {
    try {
      const isAvailable = await indexedDBHelpers.isAvailable()
      if (!isAvailable) return

      if (key.startsWith(STORAGE_KEYS.GROUP_PREFIX)) {
        const groups = (await indexedDBHelpers.getAllGroups()) || []
        const groupId = key.replace(STORAGE_KEYS.GROUP_PREFIX, '')
        const filteredGroups = groups.filter(g => g.id !== groupId)
        await indexedDBHelpers.setAllGroups(filteredGroups)
      }

      logStorage('IDB_REMOVE', key)
    } catch (error) {
      logStorage('IDB_REMOVE', key, undefined, error)
    }
  }
}

// --- New Storage Manager ---
class NewStorageManager {
  private localStorage = new LocalStorageLayer()
  private sessionStorage = new SessionStorageLayer()
  private extensionStorage = new ExtensionStorageLayer()
  private indexedDBStorage = new IndexedDBLayer()

  private writeTimers = new Map<string, NodeJS.Timeout>()

  // Read with priority: Local â†’ Session â†’ Extension â†’ IndexedDB
  async get(key: string): Promise<any> {
    logStorage('MULTI_GET_START', key)

    // Try local storage first
    let value = await this.localStorage.get(key)
    if (value !== null && value !== undefined) {
      logStorage('MULTI_GET_SUCCESS', key, { source: 'localStorage', value })
      return value
    }

    // Try session storage
    value = await this.sessionStorage.get(key)
    if (value !== null && value !== undefined) {
      logStorage('MULTI_GET_SUCCESS', key, { source: 'sessionStorage', value })
      return value
    }

    // Try extension storage
    value = await this.extensionStorage.get(key)
    if (value !== null && value !== undefined) {
      logStorage('MULTI_GET_SUCCESS', key, { source: 'extensionStorage', value })
      return value
    }

    // Try IndexedDB as fallback
    value = await this.indexedDBStorage.get(key)
    if (value !== null && value !== undefined) {
      logStorage('MULTI_GET_SUCCESS', key, { source: 'indexedDB', value })
      return value
    }

    logStorage('MULTI_GET_FAILED', key)
    return null
  }

  // Write with progressive timers across all layers
  async set(key: string, value: any, timestamp?: number): Promise<void> {
    // Ensure the value is serializable before storage
    const cleanValue = ensureSerializable(value)

    const finalValue = {
      data: cleanValue,
      timestamp: timestamp || Date.now()
    }

    logStorage('MULTI_SET_START', key, finalValue)

    // Clear any existing timer for this key
    if (this.writeTimers.has(key)) {
      clearTimeout(this.writeTimers.get(key)!)
    }

    try {
      // Immediate write to local storage
      await this.localStorage.set(key, finalValue)

      // Progressive writes with timers
      setTimeout(async () => {
        try {
          await this.sessionStorage.set(key, finalValue)
        } catch (error) {
          logStorage('MULTI_SET_SESSION_FAILED', key, undefined, error)
        }
      }, 100)

      setTimeout(async () => {
        try {
          await this.extensionStorage.set(key, finalValue)
        } catch (error) {
          logStorage('MULTI_SET_EXTENSION_FAILED', key, undefined, error)
        }
      }, 500)

      setTimeout(async () => {
        try {
          await this.indexedDBStorage.set(key, finalValue)
        } catch (error) {
          logStorage('MULTI_SET_INDEXED_FAILED', key, undefined, error)
        }
      }, 1000)

      logStorage('MULTI_SET_SUCCESS', key, finalValue)
    } catch (error) {
      logStorage('MULTI_SET_FAILED', key, undefined, error)
      throw error
    }
  }

  // Remove from all layers
  async remove(key: string): Promise<void> {
    logStorage('MULTI_REMOVE', key)

    // Clear any pending timer
    if (this.writeTimers.has(key)) {
      clearTimeout(this.writeTimers.get(key)!)
      this.writeTimers.delete(key)
    }

    await Promise.allSettled([
      this.localStorage.remove(key),
      this.sessionStorage.remove(key),
      this.extensionStorage.remove(key),
      this.indexedDBStorage.remove(key)
    ])
  }

  // Conflict resolution: newer timestamp wins
  async getWithConflictResolution(key: string): Promise<any> {
    const values = await Promise.allSettled([
      this.localStorage.get(key),
      this.sessionStorage.get(key),
      this.extensionStorage.get(key),
      this.indexedDBStorage.get(key)
    ])

    let newestValue = null
    let newestTimestamp = 0

    values.forEach((result, _index) => {
      if (result.status === 'fulfilled' && result.value) {
        const timestamp = result.value.timestamp || 0
        if (timestamp > newestTimestamp) {
          newestTimestamp = timestamp
          newestValue = result.value.data
        }
      }
    })

    if (newestValue !== null) {
      logStorage('CONFLICT_RESOLUTION', key, { timestamp: newestTimestamp, value: newestValue })
      return newestValue
    }

    return null
  }
}

// --- Public API using new storage manager ---
const storageManager = new NewStorageManager()

export const newStorageHelpers = {
  // Group management with split storage
  async getEmojiGroupIndex(): Promise<Array<{ id: string; order: number }>> {
    const index = await storageManager.getWithConflictResolution(STORAGE_KEYS.GROUP_INDEX)
    return index || []
  },

  async setEmojiGroupIndex(index: Array<{ id: string; order: number }>): Promise<void> {
    await storageManager.set(STORAGE_KEYS.GROUP_INDEX, index)
  },

  async getEmojiGroup(groupId: string): Promise<EmojiGroup | null> {
    const group = await storageManager.getWithConflictResolution(
      STORAGE_KEYS.GROUP_PREFIX + groupId
    )
    return group
  },

  async setEmojiGroup(groupId: string, group: EmojiGroup): Promise<void> {
    await storageManager.set(STORAGE_KEYS.GROUP_PREFIX + groupId, group)
  },

  async removeEmojiGroup(groupId: string): Promise<void> {
    await storageManager.remove(STORAGE_KEYS.GROUP_PREFIX + groupId)
  },

  async getAllEmojiGroups(): Promise<EmojiGroup[]> {
    const index = await this.getEmojiGroupIndex()
    if (!index.length) {
      return defaultEmojiGroups
    }

    const groups = await Promise.all(
      index.map(async groupInfo => {
        const group = await this.getEmojiGroup(groupInfo.id)
        return group ? { ...group, order: groupInfo.order } : null
      })
    )

    return groups
      .filter((group): group is EmojiGroup => group !== null)
      .sort((a, b) => a.order - b.order)
  },

  async setAllEmojiGroups(groups: EmojiGroup[]): Promise<void> {
    // Update index
    const index = groups.map((group, order) => ({ id: group.id, order }))
    await this.setEmojiGroupIndex(index)

    // Update individual groups
    await Promise.all(groups.map(group => this.setEmojiGroup(group.id, group)))
  },

  // Settings management
  async getSettings(): Promise<AppSettings> {
    const settings = await storageManager.getWithConflictResolution(STORAGE_KEYS.SETTINGS)
    return { ...defaultSettings, ...settings }
  },

  async setSettings(settings: AppSettings): Promise<void> {
    const updatedSettings = { ...settings, lastModified: Date.now() }
    await storageManager.set(STORAGE_KEYS.SETTINGS, updatedSettings)
  },

  // Favorites management
  async getFavorites(): Promise<string[]> {
    const favorites = await storageManager.getWithConflictResolution(STORAGE_KEYS.FAVORITES)
    return favorites || []
  },

  async setFavorites(favorites: string[]): Promise<void> {
    await storageManager.set(STORAGE_KEYS.FAVORITES, favorites)
  },

  // Sync operations
  async backupToSync(
    groups: EmojiGroup[],
    settings: AppSettings,
    favorites: string[]
  ): Promise<void> {
    const chromeAPI = getChromeAPI()
    if (!chromeAPI?.storage?.sync) {
      logStorage('SYNC_BACKUP', 'failed', undefined, 'Chrome Sync Storage API not available')
      return
    }

    const backupData = {
      groups,
      settings,
      favorites,
      timestamp: Date.now(),
      version: '3.0'
    }

    return new Promise((resolve, reject) => {
      try {
        chromeAPI.storage.sync.set({ [SYNC_STORAGE_KEYS.BACKUP]: backupData }, () => {
          if (chromeAPI.runtime.lastError) {
            logStorage('SYNC_BACKUP', 'failed', undefined, chromeAPI.runtime.lastError)
            reject(chromeAPI.runtime.lastError)
          } else {
            logStorage('SYNC_BACKUP', 'success', backupData)
            resolve()
          }
        })
      } catch (error) {
        logStorage('SYNC_BACKUP', 'failed', undefined, error)
        reject(error)
      }
    })
  },

  async restoreFromSync(): Promise<{
    groups?: EmojiGroup[]
    settings?: AppSettings
    favorites?: string[]
    timestamp?: number
  } | null> {
    const chromeAPI = getChromeAPI()
    if (!chromeAPI?.storage?.sync) {
      logStorage('SYNC_RESTORE', 'failed', undefined, 'Chrome Sync Storage API not available')
      return null
    }

    return new Promise(resolve => {
      try {
        chromeAPI.storage.sync.get({ [SYNC_STORAGE_KEYS.BACKUP]: null }, async (result: any) => {
          if (chromeAPI.runtime.lastError) {
            logStorage('SYNC_RESTORE', 'failed', undefined, chromeAPI.runtime.lastError)
            resolve(null)
          } else {
            const backup = result[SYNC_STORAGE_KEYS.BACKUP]
            if (backup && backup.groups) {
              logStorage('SYNC_RESTORE', 'found backup', backup)

              // Restore data using the new storage system
              await this.setAllEmojiGroups(backup.groups)
              await this.setSettings(backup.settings || defaultSettings)
              await this.setFavorites(backup.favorites || [])

              resolve({
                groups: backup.groups,
                settings: backup.settings || defaultSettings,
                favorites: backup.favorites || [],
                timestamp: backup.timestamp || 0
              })
            } else {
              logStorage('SYNC_RESTORE', 'no backup found')
              resolve(null)
            }
          }
        })
      } catch (error) {
        logStorage('SYNC_RESTORE', 'failed', undefined, error)
        resolve(null)
      }
    })
  },

  // Reset to defaults
  async resetToDefaults(): Promise<void> {
    logStorage('RESET_DEFAULTS', 'start')

    try {
      await this.setAllEmojiGroups(defaultEmojiGroups)
      await this.setSettings(defaultSettings)
      await this.setFavorites([])

      logStorage('RESET_DEFAULTS', 'success')
    } catch (error) {
      logStorage('RESET_DEFAULTS', 'failed', undefined, error)
      throw error
    }
  }
}

